    size = str7.size();//返回当前字符串的大小  
    cout << size << endl;  
    size = str7.length();//返回当前字符串的长度  
    cout << size << endl;  
    bool flag;  
    flag = str7.empty();//判断当前字符串是否为空  
    cout << flag << endl;  
    int len = 10;  
    str7.resize(len, ch);
    //把字符串当前大小置为len，并用字符ch填充不足的部分  
