    str8.assign(s, 2);//用c类型字符串s开始的n个字符赋值  
    cout << str8 << endl;  
    str8.assign(len, ch);//用len个字符ch赋值给当前字符串  
    cout << str8 << endl;  
    str8.assign(str7, 0, 3);//把字符串str7中从0开始的3个字符赋给当前字符串  
