    str10.push_back('k');//把一个字符连接到当前字符串的结尾  
    cout << str10 << endl;  

    //7.string的比较  
    flag = (str9 == str10);//判断两个字符串是否相等  
    cout << flag << endl;  
    flag = (str9 != str10);//判断两个字符串是否不相等  
    cout << flag << endl;  
    flag = (str9 > str10);//判断两个字符串是否大于关系  
    cout << flag << endl;  
    flag = (str9 < str10);//判断两个字符串是否为小于关系  
    cout << flag << endl;  
    flag = (str9 >= str10);//判断两个字符串是否为大于等于关系  
