    istringstream is(str17);  
    string s1,s2,s3,s4;  
    is>>s1>>s2>>s3>>s4;
    //s1="hello,this",s2="is",s3="a",s4="test"  
    ostringstream os;  
    os<<s1<<s2<<s3<<s4;  
    cout<<os.str() << endl;  

    //system("pause");  
}  
