    setfont(&f);// 设置字体样式
    setbkmode( TRANSPARENT);//透明背景
}
/**
 得到一个小于n的随机整数
*/
int getRandom(int n){
	srand( (unsigned)time( NULL ) );//srand()函数产生一个以当前时间开始的随机种子.应该放在for等循环语句前面 不然要很长时间等待
	return rand()%n;//MAX为最大值，其随机域为0~MAX-1
}
/**
 二叉树生成之后的退出函数
*/
void exit(){
   //按任意键退出
    getch();
	closegraph();
	generationMode();
}
/**
   用来在遍历之后连接子节点和父节点的线段
*/
void link(int n){
  	setcolor(BLUE);
	setlinestyle(PS_SOLID, NULL, 3);	// 设置线宽为 2
	if(n!=0){
	   //用来连接每个节点之间的线段
	    line(tree[(n-1)/2].xx+30,tree[(n-1)/2].yy+60,tree[n].xx+30,tree[n].yy);
	}    
}
/**
  用来访问节点
*/
void visited(int n){
	if(tree[n].data!=-1){
       	//改变图标
   setTreeEnd(tree[n].xx,tree[n].yy,tree[n].size,tree[n].size); 
   setTreeRed(tree[n].xx-20,tree[n].yy-20,30,30); //设置记录顺序的角标

   //将数字转换成char类型
   char s[10];
   sprintf(s,"%d",result);
   outtextxy(tree[n].xx-13,tree[n].yy-13,s);
   result++;

   	setcolor(BLACK);
    //将数字转换成char类型
		   char str[10];
		   sprintf(str,"%d",tree[n].data);
        outtextxy(tree[n].xx+18,tree[n].yy+18,str);

		 link(n);
       //用来设置线段的颜色和宽度
		Sleep(1000);
	}
}

/**
  先序遍历
*/
void fristOrder(int n){
	if(flag==1){//先序
	  if(n<31){
    	//访问根节点
	   visited(n);
	   //访问左子节点
	   fristOrder(n*2+1);
	   //访问右子节点
	   fristOrder(n*2+2);
	}
	}else if(flag==2){//中序
	    if(n<31){

	   //访问左子节点
	   fristOrder(n*2+1);
	   	//访问根节点
	   visited(n);
	   //访问右子节点
	   fristOrder(n*2+2);
	}
	}else{//后序
	   if(n<31){

	   //访问左子节点
	   fristOrder(n*2+1);

	   //访问右子节点
	   fristOrder(n*2+2);
	    	//访问根节点
	   visited(n);
	}

	}

   return;
}
/**
  系统随机生成二叉树时用来对满二叉树进行初始化
*/
void initFullBinaryTree(){
  int index=1,tree_index=0;
   int x,y=40,temp;
   for(int i=1;i<=5;i++){
	   int j;
